<?phpclass FeedbackController extends PluginController {	function __construct() {		$this->setLayout('admin/index');	}		function index() {		$this->display('feedback/views/index', array('feedbacks' => feedback::getAll()));	}	function approve( $id ) {		if(feedback::update(feedback::TABLE_NAME, array('status' => 1), $id))			Flash::set('success', 'Feedback approved succesfully.');		else			Flash::set('error', 'Feedback could not be approved. Please contact the website adminsitrator and provide the following code: P::feedback::a');					go_to('plugin/feedback/index');	}		function unapprove( $id ) {		if(feedback::update(feedback::TABLE_NAME, array('status' => 0), $id))			Flash::set('success', 'Feedback approved succesfully.');		else			Flash::set('error', 'Feedback could not be approved. Please contact the website adminsitrator and provide the following code: P::feedback::a');					go_to('plugin/feedback/index');	}		function delete( $id ) {		if(feedback::delete(feedback::TABLE_NAME, 'id = ?', array($id)))			Flash::set('success', 'Feedback has been deleted  succesfully.');		else			Flash::set('error', 'Feedback could not be deleted. Please contact the website administrator and provide the following code: P::feedback::d');				go_to('plugin/feedback/index');	}}