<form method="POST" action="<?php echo get_url('pages/save'); ?>" id="add_page" name="page">		<fieldset>        <legend><?php echo __('Page details'); ?></legend>        <div class="form-row ">    		<div class="form-label">    			<label for="name"><?php echo __('Page title'); ?></label>    		</div>    		<div class="form-field">    		  <input type="text" id="name" name="name" size="45" title="<?php echo __('Page title. Only letters and numbers, no special characters allowed.'); ?>" />            </div>        </div>        <div class="form-row ">    		<div class="form-label">    			<label for="content"><?php echo __('Page content'); ?></label>    		</div>    		    		<div class="form-field">				<div class="paper">    		  		<textarea name="content" style="width:98%; height:300px;"></textarea>				</div>            </div>        </div>	</fieldset><div id="dropables">	<div id="dropable">        <h2><a href="#"><?php echo __('Page SEO'); ?></a></h2>		<div class="content">        <div class="form-row ">    		<div class="form-label">    			<label for="title"><?php echo __('Meta title'); ?></label>    		</div>    		    		<div class="form-field">    		  <input type="text" name="title" size="45" />            </div>        </div>        <div class="form-row ">    		<div class="form-label">    			<label for="slug"><?php echo __('Page url'); ?></label>    		</div>    		    		<div class="form-field">    		  <input type="text" name="slug" id="slug" size="45" />            </div>        </div>               <div class="form-row ">    		<div class="form-label">    			<label for="meta_keywords"><?php echo __('Meta keywords'); ?></label>    		</div>    		    		<div class="form-field">    		  <input type="text" name="meta_keywords" size="45" id="mk" />    		  <sup class="role user-type-invited">    		  	<a href="#" id="use_mk"><?php echo __('Use default'); ?></a>    		  </sup>            </div>        </div>        <div class="form-row ">    		<div class="form-label">    			<label for="meta_description"><?php echo __('Meta description'); ?></label>    		</div>    		    		<div class="form-field">    		  <input type="text" name="meta_description" id="md" size="45" />     		  <sup class="role user-type-invited">    		  	<a href="#" id="use_md"><?php echo __('Use default'); ?></a>    		  </sup>            </div>        </div>        <div class="form-row ">    		<div class="form-label">    			<label for="tags"><?php echo __('Page tags'); ?></label>    		</div>    		    		<div class="form-field">    		  <input type="text" name="tags" size="45" />            </div>        </div>		</div>	</div>	<div id="dropable">        <h2><?php echo __('Page options'); ?></h2>		<div class="content">        <div class="form-row ">    		<div class="form-label">    			<label for="parent_id"><?php echo __('Page parent'); ?></label>    		</div>    		    		<div class="form-field">    		  <select name="parent_id" class="medium-input">				<option value="0"><?php echo __('Pagina principala'); ?></option>				<?php Page::pagesToList($parent_id); ?>			</select>            </div>        </div>        <div class="form-row ">    		<div class="form-label">    			<label for="publish_time"><?php echo __('Publish date'); ?></label>    		</div>    		    		<div class="form-field">                <input type="text" size="7" name="publish_time" value="<?php echo date("Y-m-d"); ?>" id="datepicker" />              </div>        </div>        <div class="form-row ">    		<div class="form-label">    			<label for="template"><?php echo __('Template'); ?></label>    		</div>    		    		<div class="form-field">                <select name="template" class="small-input">				<option value="0"><?php echo __('Inherit'); ?></option>				<?php Template::templatesToList($params['template']); ?>                </select>            </div>        </div>        <div class="form-row ">    		<div class="form-label">    			<label for="behavior"><?php echo __('Behavior'); ?></label>    		</div>    		    		<div class="form-field">                <select name="behavior" class="small-input">				<option value="page"><?php echo __('Static page'); ?></option>					<?php foreach(Behavior::findAll() as $behavior => $title): ?>						<option value="<?php echo $behavior; ?>" <?php if($params['behavior'] == $behavior) echo 'selected=""'; ?>><?php echo $title; ?></option>					<?php endforeach; ?>                </select>            </div>        </div>        <div class="form-row ">    		<div class="form-label">    			<label for="status"><?php echo __('Status'); ?></label>    		</div>    		    		<div class="form-field">                <select name="status" class="small-input">				<option value="0" <?php if($params['status'] == 0) echo 'selected=""'; ?>><?php echo __('Draft'); ?></option>				<option value="1" <?php if($params['status'] == 1) echo 'selected=""'; ?>><?php echo __('Published'); ?></option>				<option value="2" <?php if($params['status'] == 2) echo 'selected=""'; ?>><?php echo __('Closed'); ?></option>		      	</select>            </div>        </div>        <div class="form-row ">    		<div class="form-label">    			<label for="login_required"><?php echo __('Requires login'); ?></label>    		</div>    		    		<div class="form-field">    			<input type="radio" name="login_required" value="0" /><?php echo __('Inherit'); ?>    			<input type="radio" name="login_required" value="1" /><?php echo __('Yes'); ?>    			<input type="radio" name="login_required" value="2" checked="" /><?php echo __('No'); ?>            </div>        </div>        <div class="form-row ">    		<div class="form-label">    			<label for="root"><?php echo __('It\'s root page'); ?></label>    		</div>    		    		<div class="form-field">    			<input type="radio" name="root" value="1" /><?php echo __('Yes'); ?>    			<input type="radio" name="root" value="2" checked="" /><?php echo __('No'); ?>            </div>        </div>		</div>	</div>	<?php Observer::notify('page.add.dropable'); ?></div>    <div class="form-buttons">    <input type="submit" value="<?php echo __('Save new page'); ?>" name="submit" />  </div>	</form><script type="text/javascript">jQuery("#name").keyup(function (e) {	var name = jQuery("#name").val().replace(/ /g, '_').toLowerCase();	jQuery("#slug").val(name);	var title = jQuery("#name").val();	jQuery("#title").val(title);});jQuery("#use_md").click(function(e) {	jQuery("#md").val("<?php echo setting::get('descriptionseo'); ?>");	e.preventDefault();});jQuery("#use_mk").click(function(e) {	jQuery("#mk").val("<?php echo setting::get('keywordsseo'); ?>");	e.preventDefault();});</script><?php Observer::notify('page.add.end'); ?>